                savings_data = data.d;

            },
            error: function (response) {

                alert(response.responseText);
            },
            failure: function (response) {

                alert(response.responseText);
            }
        });

    }

    function ShowHide(value, ObjectText, Objectddl) {
        if (value == "0") {
            $(Objectddl).addClass('none');
            $(ObjectText).addClass('none');
            return;
        }
        if (value == 3 || value == 4) {

            $(Objectddl).addClass('none');
            $(ObjectText).removeClass('none');
            if (value == "4") {
                $(ObjectText).watermark('Please type the Address i.e  king burry');
            } else {

                $(ObjectText).watermark('Please type the postcode i.e HA2 0DU');

            }



        } else {

            $(Objectddl).removeClass('none');
            $(ObjectText).addClass('none');


        }
    }
    function SetValue() {




        boBookingInformation.Oneway = !$('[id*=chkreturnjourney]').is(':checked');
        boBookingInformation.Twoway = $('[id*=chkreturnjourney]').is(':checked');

        boBookingInformation.pickUpLocationTypeID = $(".clsLT option:selected").val();
        boBookingInformation.pickUpLocationTypevalue = $(".clsLT option[value=" + $(".clsLT option:selected").val() + "]").text();
        boBookingInformation.DropLocationTypeID = $(".clsLTToValue option:selected").val();
        boBookingInformation.DropLocationTypevalue = $(".clsLTToValue option[value=" + $(".clsLTToValue option:selected").val() + "]").text();
        boBookingInformation.pickUpLocationAddressID = $("#ctl00_ContentPlaceHolder1_Booking_hdfFromID").val();
            boBookingInformation.DropLocationAddressID = $("#ctl00_ContentPlaceHolder1_Booking_hdfToID").val();


            if ($("#ctl00_ContentPlaceHolder1_Booking_hdfLocationTypeFrom").val() == 3 || $("#ctl00_ContentPlaceHolder1_Booking_hdfLocationTypeFrom").val() == 4) {
                boBookingInformation.pickUpLocationAddressvalue = $(".clsAddressFrom").val().replace("'", "~");
            }
            else {
                boBookingInformation.pickUpLocationAddressvalue = $(".clsddlAddressFrom option[value=" + $(".clsddlAddressFrom option:selected").val() + "]").text();
            }

            if ($("#ctl00_ContentPlaceHolder1_Booking_hdfLocationTypeTo").val() == 3 || $("#ctl00_ContentPlaceHolder1_Booking_hdfLocationTypeTo").val() == 4) {
                boBookingInformation.DropLocationAddressvalue = $(".clsAddressTo").val().replace("'", "~");
            }
            else {
                boBookingInformation.DropLocationAddressvalue = $(".clsddlAddressTo option[value=" + $(".clsddlAddressTo option:selected").val() + "]").text();
            }


        }

        function GetFromToText(objTextFrom, objddlFrom, ddlvalue, IsVia) {


            if ($(objTextFrom).is(':visible')) {
                return $(objTextFrom).val().replace("'", "~");

            }
            if ($(objddlFrom).is(':visible')) {
                if (IsVia == "1") { return $(objddlFrom).find('.clsddlAddressToVia option:selected').html(); }
                else { return $(objddlFrom + " option[value=" + ddlvalue + "]").text() }

            }


        }
        function GetFromTovalue(objtextvalue, objddlvalue) {
            if ($(objtextvalue).is(':visible')) {
                return $(objtextvalue).val().replace("'", "~");

            }
            if ($(objddlvalue).is(':visible')) {
                return $(objddlvalue).html();

            }
        }

        function GetMinValue(TypeID) {
            var minValue = 3;

            if (TypeID == 3) { // For PostCode
                minValue = 2;
            }

            return minValue;
        }
        $(document).ajaxComplete(function () {
            $(".clsAddressFrom").removeClass('loading');
            $(".clsAddressTo").removeClass('loading');
            $(".clsAddressToVia").removeClass('loading');

        });


        function ShowPopup() {

            var id = '#loadingDiv';
            //Get the screen height and width
            var maskHeight = $(document).height();
            var maskWidth = $(window).width();

            //Set heigth and width to mask to fill up the whole screen
            $('#loadingDiv').css({ 'width': maskWidth, 'height': maskHeight, 'modal': true });

            var winH = $(window).height();
            var winW = $(window).width();

            $(id).css('top', maskHeight / 2 - $(id).height() / 2);
            $(id).css('left', maskWidth / 2 - $(id).width() / 2);

            $(id).show();


        }

        function ClosePopup() {
            //  $("#loadingDiv").dialog("close");
            $('#loadingDiv').hide();
            $('.window').hide();
        }
</script>

                                      
